Region = fact(
    'Region',
    ['name', 'type']
)
Village = fact(
    'Village',
    ['name', 'type']
)
Settlement = fact(
    'Settlement',
    ['name', 'type']
)
Street = fact(
    'Street',
    ['name', 'type']
)
Building = fact(
    'Building',
    ['number', 'type']
)

Letter = fact(
    'Letter',
    ['name', 'type']
)

Room = fact(
    'Room',
    ['number', 'type']
)
AddrPart = fact(
    'AddrPart',
    ['value']
)


def value(key):
    @property
    def field(self):
        return getattr(self, key)
    return field




class Region(Region):
    value = value('name')


class Village(Village):
    value = value('name')


class Settlement(Settlement):
    value = value('name')


class Street(Settlement):
    value = value('name')


class Building(Building):
    value = value('number')

class Letter(Letter):
    value = value('name')

class Room(Room):
    value = value('number')


class AddrPart(AddrPart):
    @property
    def obj(self):
        from natasha import obj

        part = self.value
        return obj.AddrPart(part.value, part.type)


DASH = eq('-')
DOT = eq('.')
SLASH = eq('|')

ADJF = gram('ADJF')
NOUN = gram('NOUN')
