#   ADDR VALUE
#
#############


LETTER = in_caseless(set('абвгдежзиклмнопрстуфхшщэюя'))

QUOTE = in_(QUOTES)

LETTER = or_(
    rule(LETTER),
    rule(QUOTE, LETTER, QUOTE)
)

VALUE = rule(
    INT,
    LETTER.optional()
)

SEP = in_(r'/\-')
ODD = in_(r'/\*')
EVEN = in_(r'/\~')

VALUE = or_(
    rule(VALUE),
    rule(VALUE, SEP, VALUE),
    rule(VALUE, SEP, LETTER),
    rule(VALUE, SEP, VALUE, EVEN),
    rule(VALUE, SEP, VALUE, ODD)
)

ADDR_VALUE = or_(
    rule(eq('№').optional(), VALUE),
    rule(VALUE)
    )

KORP_VALUE = or_(rule(LETTER),
                 rule(LETTER, SLASH.optional(), LETTER),
                 rule(LETTER, SLASH.optional(), LETTER, SLASH.optional(), LETTER),
                 rule(LETTER, SLASH.optional(), LETTER, SLASH.optional(), LETTER, SLASH.optional(), LETTER),
                 rule(LETTER, SLASH.optional(), LETTER, SLASH.optional(), LETTER, SLASH.optional(), LETTER, SLASH.optional(), LETTER),
                 rule(VALUE, SLASH.optional(), VALUE),
                 rule(VALUE, SLASH.optional(), VALUE, SLASH.optional(), VALUE),
                 rule(VALUE, SLASH.optional(), VALUE, SLASH.optional(), VALUE, SLASH.optional(), VALUE),
                 rule(VALUE, SLASH.optional(), VALUE, SLASH.optional(), VALUE, SLASH.optional(), VALUE, SLASH.optional(), VALUE)
                 )

